/**
 * Configuration constants for the application
 */

// Application version
export const VERSION = '0.0.10';

// Local storage keys
export const STORAGE_KEYS = {
  NODE_STYLES: 'arg-map-node-styles',
  WIP_STATE: 'arg-map-wip'
};

// Default node colors
export const DEFAULT_NODE_COLORS = {
  resolution: '#f0e6f6',
  because: '#e6f6e6',
  but: '#f6e6e6',
  however: '#e6e6f6'
};

// Support links
export const SUPPORT_LINKS = {
  PATREON: 'https://patreon.com/yourcompany',
  KOFI: 'https://ko-fi.com/yourcompany',
  REVOLUT: 'https://revolut.me/yourcompany'
};

// Company info
export const COMPANY_INFO = {
  NAME: 'Your Company Name',
  WEBSITE: 'https://yourcompany.com',
  EMAIL: 'contact@yourcompany.com'
};

// Sample resolutions are in validationUtils.js

// Maximum lengths
export const MAX_LENGTHS = {
  RESOLUTION: 300,
  NODE_TEXT: 300
};

// Meta information
export const META = {
  TITLE: 'Argument Mapping Tool',
  DESCRIPTION: 'Create visual argument maps to analyze and strengthen your reasoning',
  KEYWORDS: 'argument mapping, critical thinking, debate, logic, reasoning'
};
