import React, { useState, useRef, useCallback, useContext, useEffect } from 'react';
import ReactFlow, { 
  addEdge, 
  Controls, 
  Background, 
  useNodesState, 
  useEdgesState, 
  MarkerType,
  Panel
} from 'reactflow';
import 'reactflow/dist/style.css';
import { AppContext } from '../../context/AppContext';
import CustomNode from './CustomNode';
import { exportToImage, exportToTrie, importFromTrie } from '../../utils/exportUtils';

// Define node types and their default colors
const NODE_TYPES = {
  resolution: { label: 'Resolution', color: { background: '#f0e6f6', text: '#000000' } },
  because: { label: 'Because', color: { background: '#e6f6e6', text: '#000000' } },
  but: { label: 'But', color: { background: '#f6e6e6', text: '#000000' } },
  however: { label: 'However', color: { background: '#e6e6f6', text: '#000000' } }
};

// Main ArgumentMap component
const ArgumentMap = ({ initialResolution }) => {
  const { 
    saveWipState, 
    loadWipState, 
    nodeStyles, 
    setCurrentMap,
    deleteNode 
  } = useContext(AppContext);

  const reactFlowWrapper = useRef(null);
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);

  // Initialize the resolution node when component mounts or resolution changes
  useEffect(() => {
    if (initialResolution && !nodes.length) {
      // Start with a delay to ensure container is ready
      setTimeout(() => {
        const initialNodes = [
          {
            id: '1',
            type: 'custom',
            position: { x: 350, y: 50 },
            data: { 
              label: initialResolution,
              type: 'resolution',
            },
          },
        ];
        setNodes(initialNodes);
      }, 100);
    }
  }, [initialResolution, setNodes, nodes.length]);

  // Save current map state to context when it changes
  useEffect(() => {
    if (nodes.length > 0) {
      setCurrentMap({ nodes, edges });
      saveWipState({ nodes, edges });
    }
  }, [nodes, edges, setCurrentMap, saveWipState]);

  // Try to load saved WIP state when component mounts
  useEffect(() => {
    const savedState = loadWipState();
    if (savedState && savedState.nodes && savedState.nodes.length > 0) {
      setNodes(savedState.nodes);
      setEdges(savedState.edges || []);
    }
  }, [loadWipState, setNodes, setEdges]);

  // Add a new node connected to a parent node
  const addNode = useCallback((parentId, nodeType) => {
    console.log("ADDING NODE:", parentId, nodeType);

    // Find the parent node to position the new node relatively
    const parentNode = nodes.find(node => node.id === parentId);
    if (!parentNode) {
      console.error("Parent node not found:", parentId);
      return;
    }

    // Count existing children to calculate position
    const existingChildren = edges.filter(edge => edge.source === parentId).length;

    // Calculate horizontal offset based on number of children
    // First child is centered, subsequent children are offset left/right
    let xOffset = 0;
    if (existingChildren > 0) {
      // Alternate left and right for odd/even children
      const direction = existingChildren % 2 === 0 ? 1 : -1;
      // Increase offset for each pair of children
      const magnitude = Math.ceil(existingChildren / 2) * 200;
      xOffset = direction * magnitude;
    }

    const newNodeId = (nodes.length + 1).toString();
    const newNode = {
      id: newNodeId,
      type: 'custom',
      position: { 
        x: parentNode.position.x + xOffset,
        y: parentNode.position.y + 180
      },
      data: { 
        label: `Enter your ${nodeType} statement`,
        type: nodeType,
      },
    };

    // Get color for this node type (for edge color)
    const nodeTypeColor = nodeStyles[nodeType]?.background || 
                          NODE_TYPES[nodeType]?.color?.background || 
                          '#cccccc';

    const newEdge = {
      id: `e${parentId}-${newNodeId}`,
      source: parentId,
      target: newNodeId,
      type: 'default',
      animated: false,
      style: { stroke: nodeTypeColor, strokeWidth: 2 },
      markerEnd: {
        type: MarkerType.ArrowClosed,
        color: nodeTypeColor
      },
    };

    setNodes(nds => [...nds, newNode]);
    setEdges(eds => [...eds, newEdge]);

    // Fit view after adding nodes
    setTimeout(() => {
      if (reactFlowInstance) {
        reactFlowInstance.fitView({ padding: 0.2 });
      }
    }, 50);
  }, [nodes, edges, nodeStyles, setNodes, setEdges, reactFlowInstance]);

  // Handle connections between nodes
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge({
      ...params,
      style: { stroke: '#cccccc', strokeWidth: 2 },
      markerEnd: {
        type: MarkerType.ArrowClosed,
      },
    }, eds)),
    [setEdges]
  );

  // Export the argument map as an image
  const handleExportImage = useCallback(() => {
    if (reactFlowInstance && reactFlowWrapper.current) {
      exportToImage(reactFlowWrapper.current, 'argument-map.png');
    }
  }, [reactFlowInstance]);

  // Export the argument map as a .trie file
  const handleExportTrie = useCallback(() => {
    if (nodes.length > 0) {
      exportToTrie({ nodes, edges, styles: nodeStyles }, 'argument-map.trie');
    }
  }, [nodes, edges, nodeStyles]);

  // Import from a .trie file
  const handleImportTrie = useCallback(async (event) => {
    try {
      const file = event.target.files[0];
      const importedData = await importFromTrie(file);

      if (importedData && importedData.nodes && importedData.edges) {
        setNodes(importedData.nodes);
        setEdges(importedData.edges);
      }
    } catch (error) {
      console.error('Error importing file:', error);
      alert('Failed to import the file. Please ensure it is a valid .trie file.');
    }
  }, [setNodes, setEdges]);

  // Update node positions after drag
  const onNodeDragStop = useCallback((_, node) => {
    setNodes((prevNodes) =>
      prevNodes.map((n) => (n.id === node.id ? { ...n, position: node.position } : n))
    );
  }, [setNodes]);

  const handleInit = (instance) => {
    setReactFlowInstance(instance);

    // Force a layout refresh on init
    setTimeout(() => {
      instance.fitView({ padding: 0.2 });
      instance.zoomTo(1.0); // Set a fixed zoom level

      // Center the viewport
      if (nodes.length > 0) {
        const node = nodes[0];
        instance.setCenter(node.position.x, node.position.y);
      }
    }, 200);
  };

  // Define the custom node type with direct access to the addNode function
  const nodeTypes = {
    custom: (props) => <CustomNode {...props} onAddChild={addNode} />
  };

  return (
    <div style={{ width: '100%', height: '600px', border: '1px solid #eee' }}> {/* Fixed height container */}
      <div ref={reactFlowWrapper} style={{ width: '100%', height: '100%' }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onInit={handleInit}
          onNodeDragStop={onNodeDragStop}
          nodeTypes={nodeTypes}
          fitView
          fitViewOptions={{ padding: 0.2 }}
          minZoom={0.2}
          maxZoom={2}
          defaultViewport={{ x: 0, y: 0, zoom: 1 }}
        >
          <Controls position="bottom-right" />
          <Background color="#f8f8f8" gap={16} size={1} variant="dots" />

          <Panel position="top-left" style={{ margin: '10px' }}>
            <div style={{ 
              display: 'flex', 
              gap: '10px',
              background: 'rgba(255, 255, 255, 0.8)',
              padding: '5px',
              borderRadius: '4px'
            }}>
              <button 
                onClick={handleExportImage}
                style={{
                  padding: '5px 10px',
                  fontSize: '12px',
                  backgroundColor: '#f0f0f0',
                  border: '1px solid #ccc',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >Export as PNG</button>
              <button 
                onClick={handleExportTrie}
                style={{
                  padding: '5px 10px',
                  fontSize: '12px',
                  backgroundColor: '#f0f0f0',
                  border: '1px solid #ccc',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >Export as .trie</button>
              <label className="import-button" style={{ 
                padding: '5px 10px', 
                fontSize: '12px',
                backgroundColor: '#f0f0f0', 
                borderRadius: '4px', 
                cursor: 'pointer',
                border: '1px solid #ccc',
                margin: 0
              }}>
                Import .trie
                <input 
                  type="file" 
                  accept=".trie" 
                  onChange={handleImportTrie} 
                  style={{ display: 'none' }} 
                />
              </label>
            </div>
          </Panel>
        </ReactFlow>
      </div>
    </div>
  );
};

export default ArgumentMap;
