// Main ArgumentMap component
const ArgumentMap = ({ initialResolution }) => {
  const { 
    saveWipState, 
    loadWipState, 
    nodeStyles, 
    setCurrentMap 
  } = useContext(AppContext);

  const reactFlowWrapper = useRef(null);
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);

  // Initialize the resolution node when component mounts or resolution changes
  useEffect(() => {
    if (initialResolution && !nodes.length) {
      const initialNodes = [
        {
          id: '1',
          type: 'default',
          position: { x: 250, y: 25 },
          data: { 
            label: initialResolution,
            type: 'resolution',
          },
        },
      ];
      setNodes(initialNodes);
    }
  }, [initialResolution, setNodes, nodes.length]);

  // Save current map state to context when it changes
  useEffect(() => {
    if (nodes.length > 0) {
      setCurrentMap({ nodes, edges });
      saveWipState({ nodes, edges });
    }
  }, [nodes, edges, setCurrentMap, saveWipState]);

  // Try to load saved WIP state when component mounts
  useEffect(() => {
    const savedState = loadWipState();
    if (savedState && savedState.nodes && savedState.nodes.length > 0) {
      setNodes(savedState.nodes);
      setEdges(savedState.edges || []);
    }
  }, [loadWipState, setNodes, setEdges]);

  // Add a new node connected to a parent node
  const addNode = useCallback((parentId, nodeType) => {
    // Find the parent node to position the new node relatively
    const parentNode = nodes.find(node => node.id === parentId);
    if (!parentNode) return;

    const newNodeId = (nodes.length + 1).toString();
    const newNode = {
      id: newNodeId,
      type: 'default',
      position: { 
        x: parentNode.position.x + (Math.random() * 50) - 25,
        y: parentNode.position.y + 150
      },
      data: { 
        label: `Enter your ${nodeType} statement`,
        type: nodeType,
      },
    };

    const newEdge = {
      id: `e${parentId}-${newNodeId}`,
      source: parentId,
      target: newNodeId,
      type: 'default',
      markerEnd: {
        type: MarkerType.ArrowClosed,
      },
    };

    setNodes(nds => [...nds, newNode]);
    setEdges(eds => [...eds, newEdge]);
  }, [nodes, setNodes, setEdges]);

  // Set the context for NodeButton component
  useEffect(() => {
    // Register addNode function to be accessible by NodeButton
    window.addNodeToArgumentMap = addNode;
  }, [addNode]);

  // Handle connections between nodes
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  // Export the argument map as an image
  const handleExportImage = useCallback(() => {
    if (reactFlowInstance && reactFlowWrapper.current) {
      exportToImage(reactFlowWrapper.current, 'argument-map.png');
    }
  }, [reactFlowInstance]);

  // Export the argument map as a .trie file
  const handleExportTrie = useCallback(() => {
    if (nodes.length > 0) {
      exportToTrie({ nodes, edges, styles: nodeStyles }, 'argument-map.trie');
    }
  }, [nodes, edges, nodeStyles]);

  // Import from a .trie file
  const handleImportTrie = useCallback(async (event) => {
    try {
      const file = event.target.files[0];
      const importedData = await importFromTrie(file);

      if (importedData && importedData.nodes && importedData.edges) {
        setNodes(importedData.nodes);
        setEdges(importedData.edges);
      }
    } catch (error) {
      console.error('Error importing file:', error);
      alert('Failed to import the file. Please ensure it is a valid .trie file.');
    }
  }, [setNodes, setEdges]);

  // Update node positions after drag
  const onNodeDragStop = useCallback((_, node) => {
    setNodes((prevNodes) =>
      prevNodes.map((n) => (n.id === node.id ? { ...n, position: node.position } : n))
    );
  }, [setNodes]);

  return (
    <div className="argument-map-container" style={{ height: 'calc(100vh - 150px)', width: '100%' }}>
      <div className="argument-map-actions" style={{ 
        display: 'flex', 
        gap: '10px',
        position: 'absolute',
        zIndex: 5,
        top: '10px',
        left: '10px',
        background: 'rgba(255, 255, 255, 0.8)',
        padding: '5px',
        borderRadius: '4px'
      }}>
        <button 
          onClick={handleExportImage}
          style={{
            padding: '5px 10px',
            fontSize: '12px',
            backgroundColor: '#f0f0f0',
            border: '1px solid #ccc',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >Export as PNG</button>
        <button 
          onClick={handleExportTrie}
          style={{
            padding: '5px 10px',
            fontSize: '12px',
            backgroundColor: '#f0f0f0',
            border: '1px solid #ccc',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >Export as .trie</button>
        <label className="import-button" style={{ 
          padding: '5px 10px', 
          fontSize: '12px',
          backgroundColor: '#f0f0f0', 
          borderRadius: '4px', 
          cursor: 'pointer',
          border: '1px solid #ccc',
          margin: 0
        }}>
          Import .trie
          <input 
            type="file" 
            accept=".trie" 
            onChange={handleImportTrie} 
            style={{ display: 'none' }} 
          />
        </label>
      </div>

      <div ref={reactFlowWrapper} style={{ height: '100%', width: '100%' }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onInit={setReactFlowInstance}
          onNodeDragStop={onNodeDragStop}
          nodeTypes={{ default: CustomNode }}
          fitView
        >
          <Controls />
          <Background color="#f8f8f8" variant="dots" />
        </ReactFlow>
      </div>
    </div>
  );
};

export default ArgumentMap;
