import React, { useState, useEffect, useRef } from 'react';

const ColorPickerPopup = ({ initialColor, onColorChange, onClose, title }) => {
  const [color, setColor] = useState(initialColor || '#ffffff');
  const [r, setR] = useState(255);
  const [g, setG] = useState(255);
  const [b, setB] = useState(255);
  const [isDragging, setIsDragging] = useState(false);
  const pickerRef = useRef(null);

  // Parse RGB values from initial color on mount
  useEffect(() => {
    if (initialColor) {
      if (initialColor.startsWith('#')) {
        const hex = initialColor.replace('#', '');
        const r = parseInt(hex.substring(0, 2), 16);
        const g = parseInt(hex.substring(2, 4), 16);
        const b = parseInt(hex.substring(4, 6), 16);
        setR(r);
        setG(g);
        setB(b);
      }
    }
  }, [initialColor]);

  // Convert RGB to HEX
  const rgbToHex = (r, g, b) => {
    return '#' + [r, g, b].map(x => {
      const hex = x.toString(16);
      return hex.length === 1 ? '0' + hex : hex;
    }).join('');
  };

  // Update RGB and color when RGB inputs change
  const handleRGBChange = (value, setter, component) => {
    const numValue = parseInt(value, 10);
    if (!isNaN(numValue) && numValue >= 0 && numValue <= 255) {
      setter(numValue);
      let newColor;
      if (component === 'r') {
        newColor = rgbToHex(numValue, g, b);
      } else if (component === 'g') {
        newColor = rgbToHex(r, numValue, b);
      } else {
        newColor = rgbToHex(r, g, numValue);
      }
      setColor(newColor);
    }
  };

  // Handle clicks outside the color picker to close it
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (pickerRef.current && !pickerRef.current.contains(event.target)) {
        onClose();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [onClose]);

  // Apply color and close
  const handleApply = () => {
    onColorChange(color);
    onClose();
  };

  // Handle gradient click
  const handleGradientClick = (e) => {
    if (!isDragging) return;
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Calculate color from position
    const saturation = (x / rect.width) * 100;
    const brightness = 100 - ((y / rect.height) * 100);

    // Convert HSV to RGB (simplified)
    // In this simplified version, we're just mapping x to R-B and y to brightness
    const r = Math.round(255 * (1 - x / rect.width) * (brightness / 100));
    const b = Math.round(255 * (x / rect.width) * (brightness / 100));
    const g = Math.round((255 - Math.abs(128 - (x / rect.width) * 255)) * (brightness / 100));

    setR(r);
    setG(g);
    setB(b);
    setColor(rgbToHex(r, g, b));
  };

  return (
    <div 
      ref={pickerRef}
      className="color-picker-popup"
      style={{
        position: 'absolute',
        zIndex: 1000,
        background: '#333',
        borderRadius: '8px',
        padding: '15px',
        width: '230px',
        boxShadow: '0 5px 15px rgba(0,0,0,0.5)',
        color: 'white',
        userSelect: 'none'
      }}
    >
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '10px'
      }}>
        <span>{title || 'Color Picker'}</span>
        <button 
          onClick={onClose}
          style={{
            background: 'transparent',
            border: 'none',
            color: 'white',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >×</button>
      </div>

      <div 
        className="color-preview"
        style={{
          width: '100%',
          height: '40px',
          background: color,
          borderRadius: '4px',
          marginBottom: '10px',
          border: '1px solid rgba(255,255,255,0.2)'
        }}
      ></div>

      <div 
        className="color-gradient"
        style={{
          width: '100%',
          height: '150px',
          background: 'linear-gradient(to right, white, blue), linear-gradient(to top, black, transparent)',
          backgroundBlendMode: 'multiply',
          borderRadius: '4px',
          marginBottom: '10px',
          cursor: 'crosshair',
          position: 'relative',
          border: '1px solid rgba(255,255,255,0.2)'
        }}
        onMouseDown={() => setIsDragging(true)}
        onMouseUp={() => setIsDragging(false)}
        onMouseLeave={() => setIsDragging(false)}
        onMouseMove={handleGradientClick}
        onClick={handleGradientClick}
      >
        <div 
          style={{
            position: 'absolute',
            border: '2px solid white',
            borderRadius: '50%',
            width: '10px',
            height: '10px',
            transform: 'translate(-50%, -50%)',
            pointerEvents: 'none',
            boxShadow: '0 0 0 1px black'
          }}
        ></div>
      </div>

      <div className="color-slider" style={{ marginBottom: '15px' }}>
        <input 
          type="range" 
          min="0" 
          max="359" 
          style={{
            width: '100%',
            height: '20px',
            background: 'linear-gradient(to right, red, yellow, lime, cyan, blue, magenta, red)',
            borderRadius: '4px',
            appearance: 'none',
            outline: 'none'
          }}
        />
      </div>

      <div className="rgb-inputs" style={{ display: 'flex', gap: '10px', marginBottom: '15px' }}>
        <div style={{ flex: 1, textAlign: 'center' }}>
          <input 
            type="text" 
            value={r}
            onChange={(e) => handleRGBChange(e.target.value, setR, 'r')}
            style={{
              width: '100%',
              padding: '5px',
              background: '#555',
              border: 'none',
              borderRadius: '4px',
              textAlign: 'center',
              color: 'white',
              marginBottom: '5px'
            }}
          />
          <div>R</div>
        </div>
        <div style={{ flex: 1, textAlign: 'center' }}>
          <input 
            type="text" 
            value={g}
            onChange={(e) => handleRGBChange(e.target.value, setG, 'g')}
            style={{
              width: '100%',
              padding: '5px',
              background: '#555',
              border: 'none',
              borderRadius: '4px',
              textAlign: 'center',
              color: 'white',
              marginBottom: '5px'
            }}
          />
          <div>G</div>
        </div>
        <div style={{ flex: 1, textAlign: 'center' }}>
          <input 
            type="text" 
            value={b}
            onChange={(e) => handleRGBChange(e.target.value, setB, 'b')}
            style={{
              width: '100%',
              padding: '5px',
              background: '#555',
              border: 'none',
              borderRadius: '4px',
              textAlign: 'center',
              color: 'white',
              marginBottom: '5px'
            }}
          />
          <div>B</div>
        </div>
      </div>

      <button
        onClick={handleApply}
        style={{
          width: '100%',
          padding: '8px',
          background: '#1a73e8',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        Apply
      </button>
    </div>
  );
};

export default ColorPickerPopup;
