import React, { useState, useRef, useCallback, useContext, useEffect } from 'react';
import ReactFlow, { 
  addEdge, 
  Controls, 
  Background, 
  useNodesState, 
  useEdgesState, 
  MarkerType 
} from 'reactflow';
import 'reactflow/dist/style.css';
import { AppContext } from '../../context/AppContext';
import NodeButton from './NodeButton';
import { validateStatement } from '../../utils/validationUtils';
import { exportToImage, exportToTrie, importFromTrie } from '../../utils/exportUtils';

// Define node types and their default colors
const NODE_TYPES = {
  resolution: { label: 'Resolution', color: '#f0e6f6' },
  because: { label: 'Because', color: '#e6f6e6' },
  but: { label: 'But', color: '#f6e6e6' },
  however: { label: 'However', color: '#e6e6f6' }
};

// Custom node component
const CustomNode = ({ data, id }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [nodeText, setNodeText] = useState(data.label);
  const [showSettings, setShowSettings] = useState(false);
  const inputRef = useRef(null);
  const nodeRef = useRef(null);
  const { updateNodeText, nodeStyles } = useContext(AppContext);

  // Get the appropriate color based on node type and custom styles
  const nodeColor = nodeStyles[data.type] || NODE_TYPES[data.type].color;

  const handleDoubleClick = () => {
    setIsEditing(true);
    setTimeout(() => {
      if (inputRef.current) {
        inputRef.current.focus();
      }
    }, 0);
  };

  const handleInputBlur = () => {
    if (validateStatement(nodeText)) {
      updateNodeText(id, nodeText);
      setIsEditing(false);
    } else {
      alert('Please enter a valid statement (a complete sentence with at least 3 words).');
      setNodeText(data.label);
      setIsEditing(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleInputBlur();
    }
  };

  const handleShowInfo = (e) => {
    e.stopPropagation();
    const nodeType = data.type;
    const infoMessages = {
      resolution: "This is your main claim or proposition. It forms the foundation of your argument.",
      because: "Supporting evidence or reason that strengthens your main claim.",
      but: "Counterargument or opposing point that challenges your claim.",
      however: "Qualifying statement or additional context to your argument."
    };

    alert(infoMessages[nodeType] || "Add details to build your argument.");
  };

  return (
    <div 
      ref={nodeRef}
      className="node-wrapper"
      style={{ 
        background: nodeColor,
        borderRadius: '8px',
        padding: '16px', 
        minWidth: '250px',
        maxWidth: '400px',
        boxShadow: '0 3px 5px rgba(0, 0, 0, 0.15)',
        border: `2px solid ${data.selected ? '#1a73e8' : 'transparent'}`,
        position: 'relative',
        wordBreak: 'break-word'
      }}
      onDoubleClick={handleDoubleClick}
      onMouseEnter={() => setShowSettings(true)}
      onMouseLeave={() => setShowSettings(false)}
    >
      {/* Settings and Info buttons that appear on hover */}
      {showSettings && !isEditing && (
        <div className="node-controls" style={{
          position: 'absolute',
          top: '0',
          right: '0',
          display: 'flex',
          gap: '2px'
        }}>
          {/* Settings Button */}
          <button
            onClick={() => document.querySelector('.node-customization')?.scrollIntoView({ behavior: 'smooth' })}
            style={{
              background: 'rgba(255, 255, 255, 0.8)',
              border: 'none',
              borderRadius: '0 8px 0 8px',
              padding: '6px',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
            title="Settings"
          >
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <circle cx="12" cy="12" r="3"></circle>
              <path d="M19.4 15a1.65 1.65 0 0 0 .33 1.82l.06.06a2 2 0 0 1 0 2.83 2 2 0 0 1-2.83 0l-.06-.06a1.65 1.65 0 0 0-1.82-.33 1.65 1.65 0 0 0-1 1.51V21a2 2 0 0 1-2 2 2 2 0 0 1-2-2v-.09A1.65 1.65 0 0 0 9 19.4a1.65 1.65 0 0 0-1.82.33l-.06.06a2 2 0 0 1-2.83 0 2 2 0 0 1 0-2.83l.06-.06a1.65 1.65 0 0 0 .33-1.82 1.65 1.65 0 0 0-1.51-1H3a2 2 0 0 1-2-2 2 2 0 0 1 2-2h.09A1.65 1.65 0 0 0 4.6 9a1.65 1.65 0 0 0-.33-1.82l-.06-.06a2 2 0 0 1 0-2.83 2 2 0 0 1 2.83 0l.06.06a1.65 1.65 0 0 0 1.82.33H9a1.65 1.65 0 0 0 1-1.51V3a2 2 0 0 1 2-2 2 2 0 0 1 2 2v.09a1.65 1.65 0 0 0 1 1.51 1.65 1.65 0 0 0 1.82-.33l.06-.06a2 2 0 0 1 2.83 0 2 2 0 0 1 0 2.83l-.06.06a1.65 1.65 0 0 0-.33 1.82V9a1.65 1.65 0 0 0 1.51 1H21a2 2 0 0 1 2 2 2 2 0 0 1-2 2h-.09a1.65 1.65 0 0 0-1.51 1z"></path>
