              background: 'rgba(255, 255, 255, 0.8)',
              border: 'none',
              borderRadius: '0 0 0 8px',
              padding: '6px',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
