        </div>
      )}

      <div className="node-header" style={{ 
        fontWeight: 'bold', 
        marginBottom: '12px', 
        fontSize: '16px',
        textAlign: 'center'
      }}>
        {NODE_TYPES[data.type].label}
      </div>

      {isEditing ? (
        <input
          ref={inputRef}
          type="text"
          value={nodeText}
          onChange={(e) => setNodeText(e.target.value)}
          onBlur={handleInputBlur}
          onKeyDown={handleKeyDown}
          style={{ 
            width: '100%', 
            padding: '8px',
            borderRadius: '4px',
            border: '1px solid #ccc',
            marginBottom: '12px',
            fontSize: '14px'
          }}
